INSERT INTO `config_info` VALUES (1, 'application-dev.yml', 'DEFAULT_GROUP', '# 加解密根密码\njasypt:\n  encryptor:\n    password: pig #根密码\n\n# Spring 相关\nspring: \n    # redis配置 \n  redis:\n        # Redis数据库索引（默认为0）\n      database: 0\n        # Redis服务器地址\n      host: 127.0.0.1\n        # Redis服务器连接端口\n      port: 6379\n        # Redis服务器连接密码（默认为空）\n        # password: \n        # 连接超时时间\n      timeout: 10s\n      lettuce:\n          pool:\n                # 连接池最大连接数\n              max-active: 200\n                # 连接池最大阻塞等待时间（使用负值表示没有限制）\n              max-wait: -1ms\n                # 连接池中的最大空闲连接\n              max-idle: 10\n                # 连接池中的最小空闲连接\n              min-idle: 0\n\n  cloud:\n    sentinel:\n      eager: true\n      transport:\n        dashboard: just-sentinel:5003\n      datasource:\n        flow: \n          nacos: \n            server-addr: ${NACOS_HOST:just-register}:${NACOS_PORT:8848}\n            dataId: ${spring.application.name}-sentinel-flow\n            groupId: DEFAULT_GROUP\n            data-type: json\n            rule-type: flow\n        authority: \n          nacos: \n            server-addr: ${NACOS_HOST:just-register}:${NACOS_PORT:8848}\n            dataId: ${spring.application.name}-sentinel-authority\n            groupId: DEFAULT_GROUP\n            data-type: json\n            rule-type: authority\n        degrade: \n          nacos: \n            server-addr: ${NACOS_HOST:just-register}:${NACOS_PORT:8848}\n            dataId: ${spring.application.name}-sentinel-degrade\n            groupId: DEFAULT_GROUP\n            data-type: json\n            rule-type: degrade\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n\n# feign 配置\nfeign:\n  sentinel:\n    enabled: true\n    global:\n      exception: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 10000\n        readTimeout: 10000\n  compression:\n    request: