INSERT INTO `his_config_info` VALUES (1, 97, 'application-dev.yml', 'DEFAULT_GROUP', '', '# 加解密根密码\njasypt:\n  encryptor:\n    password: pig #根密码\n\n# Spring 相关\nspring: \n    # redis配置 \n  redis:\n        # Redis数据库索引（默认为0）\n      database: 0\n        # Redis服务器地址\n      host: 127.0.0.1\n        # Redis服务器连接端口\n      port: 6379\n        # Redis服务器连接密码（默认为空）\n        # password: \n        # 连接超时时间\n      timeout: 10s\n      lettuce:\n          pool:\n                # 连接池最大连接数\n              max-active: 200\n                # 连接池最大阻塞等待时间（使用负值表示没有限制）\n              max-wait: -1ms\n                # 连接池中的最大空闲连接\n              max-idle: 10\n                # 连接池中的最小空闲连接\n              min-idle: 0\n\n  cloud:\n    sentinel:\n      eager: true\n      transport:\n        dashboard: just-sentinel:5003\n      datasource:\n        flow: \n          nacos: \n            server-addr: ${NACOS_HOST:just-register}:${NACOS_PORT:8848}\n            dataId: ${spring.application.name}-sentinel-flow\n            groupId: DEFAULT_GROUP\n            data-type: json\n            rule-type: flow\n        authority: \n          nacos: \n            server-addr: ${NACOS_HOST:just-register}:${NACOS_PORT:8848}\n            dataId: ${spring.application.name}-sentinel-authority\n            groupId: DEFAULT_GROUP\n            data-type: json\n            rule-type: authority\n# 暴露监控端点\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n\n# feign 配置\nfeign:\n  sentinel:\n    enabled: true\n    global:\n      exception: true\n  okhttp:\n    enabled: true\n  httpclient:\n    enabled: false\n  client:\n    config:\n      default:\n        connectTimeout: 10000\n        readTimeout: 10000\n  compression:\n    request:\n      enabled: true\n    response:\n      enabled: true\n\n# mybaits-plus配置\nmybatis-plus:\n  mapper-locations: classpath:/mapper/*Mapper.xml\n  global-config:\n    banner: false\n    db-config:\n      id-type: auto\n      table-underline: true\n      logic-delete-value: 1\n      logic-not-delete-value: 0\n  configuration:\n    map-underscore-to-camel-case: true\n\n# spring security 配置\nsecurity:\n  oauth2:\n    resource:\n      loadBalanced: true\n      token-info-uri: http://just-auth/oauth/check_token\n    # 通用放行URL，服务个性化，请在对应配置文件覆盖\n    ignore:\n      urls:\n        - /v2/api-docs\n        - /actuator/**\n        - /user/login\n# swagger 配置\nswagger:\n  enabled: true\n  title: Just Swagger API\n  license: Powered By just4cloud\n  licenseUrl: https://just4cloud.com\n  terms-of-service-url: https://just4cloud.com\n  contact:\n    email: wangiegie@gmail.com\n    url: https://just4cloud.com\n  authorization:\n    name: just4cloud OAuth\n    auth-regex: ^.*$\n    authorization-scope-list:\n      - scope: server\n        description: server all\n    token-url-list:\n      - http://${GATEWAY_HOST:just-gateway}:${GATEWAY-PORT:9999}/auth/oauth/token\n\n\n# Sa-Token 配置\nsa-token: \n    token-name: Authorization\n    is-read-cookie: false\n    timeout: 2992000\n    token-prefix: Bearer\n    token-style: simple-uuid\n    # OAuth2.0 配置 \n    oauth2: \n        is-code: true\n        is-implicit: true\n        is-password: true\n        is-client: true\n      ', '5aabccb078508874991260bf3dd1f680', '2010-05-05 00:00:00', '2021-12-29 20:01:20', 'nacos', '127.0.0.1', 'U', '');
